DROP TABLE IF EXISTS `bbs_questions`;
CREATE TABLE `bbs_questions`  (
  `Guid` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '唯一编号 唯一编号',
  `UserGuid` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户编号 用户编号',
  `Title` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '问题标题 问题标题',
  `EnTitle` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '英文标题，可以作为Url 英文标题，可以作为Url',
  `Types` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '问题类型 问题类型',
  `Tags` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标签 标签',
  `Point` int(11) NOT NULL COMMENT '消耗积分 消耗积分',
  `LookSum` int(11) NOT NULL DEFAULT 0 COMMENT '查看次数 查看次数',
  `AnswerSum` int(11) NOT NULL DEFAULT 0 COMMENT '答案次数 答案次数',
  `Support` int(11) NOT NULL DEFAULT 0 COMMENT '投票次数=赞 投票次数=赞',
  `Status` int(11) NOT NULL DEFAULT 1 COMMENT '状态 1=未解决2=未回答3=已解决',
  `IsRed` bit(1) NOT NULL DEFAULT b'0' COMMENT '热门状态 热门状态',
  `Audit` bit(1) NOT NULL DEFAULT b'1' COMMENT '审核状态',
  `Contents` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '问题内容 问题内容',
  `Summary` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '描述',
  `AddTime` datetime(0) NOT NULL COMMENT '发布时间 发布时间',
  PRIMARY KEY (`Guid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = ' ' ROW_FORMAT = Dynamic;

