INSERT INTO `bbs_questions` VALUES ('4773ceaa-ebc2-451b-bfef-ef74fd89b1d5', '1ba2b9fc-aed6-452d-91df-36e3afada4f5', '这是问题的标题试试', '2019061500001', 'effcee5a-cb08-4221-8a6b-d2eb94c3a93c', 'javascript,cms', 0, 32, 0, 0, 1, b'0', b'1', '<p>少时诵诗书所所所所所所所所所所所所所所所所所所</p>\r\n<p>ab</p>\r\n<p>ab</p>\r\n<p>c</p>\r\n<pre class=\"language-javascript\"><code>    saveQuestion: function(d) {\r\n        os.ajax(\"api/bbs/user/save/question\",\r\n            d,\r\n            function (res) {\r\n                if (res.statusCode === 200) {\r\n                    toastr.success(\'问题发表成功，请等待审核~\');\r\n                    setTimeout(() =&gt; {\r\n                        window.location = \"/bbs\";\r\n                    }, 1000);\r\n                } else {\r\n                    toastr.error(res.message);\r\n                }\r\n            });\r\n    },</code></pre>', '//当表格数据加载完成时\r\n\r\n        table.on(\'load-success.bs.table\', function (e, data) {\r\n            //这里可以获取从服务端获取的JSON数据', '2019-06-15 23:45:04');
INSERT INTO `bbs_questions` VALUES ('63b20d96-a4b0-4c6d-8d73-38debbf7bc4c', 'ded8a877-5530-4716-a707-7e9b71e1a937', 'sss', '2019061500002', 'effcee5a-cb08-4221-8a6b-d2eb94c3a93c', '后台开发,CMS', 0, 21, 0, 0, 1, b'1', b'1', NULL, NULL, '2019-06-12 22:28:02');
INSERT INTO `bbs_questions` VALUES ('7c747ddc-0d48-4a09-85a3-6257a157dfc4', '1ba2b9fc-aed6-452d-91df-36e3afada4f5', 'vue——删除对象数组中的某个元素', '1568270567243', 'effcee5a-cb08-4221-8a6b-d2eb94c3a93c', 'vue', 0, 21, 0, 0, 1, b'0', b'1', '<pre class=\"language-markup\"><code>//html\r\n&lt;div id=\"app\"&gt;\r\n　　//v-for循环就不写了 每一条数据最后都有一个删除的超链 .prevent阻止默认的跳转行为　只执行点击事件\r\n　　&lt;a href=\"\" @click.prevent=\"del(item.id)\"&gt;删除&lt;/a&gt;\r\n&lt;/div&gt;\r\n\r\n//script\r\n&lt;script&gt;\r\n　　var vm = new Vue({\r\n　　　　el:\'app\',\r\n　　　　data:{\r\n　　　　　　id:\'\',\r\n　　　　　　name:\'\',\r\n　　　　　　list:[\r\n　　　　　　　　{id:1, name:\'惊鲵\'}，\r\n　　　　　　　　{id:2, name:\'掩日\'}，\r\n　　　　　　　　{id:2, name:\'黑白玄翦\'}\r\n　　　　　　]\r\n　　　　},\r\n　　　　methods:{//methods中定义了当前vue实例中所有可用的方法\r\n　　　　　　del(id){\r\n　　　　　　　　this.list.some((item, i)=&gt;{\r\n　　　　　　　　　　if(item.id==id){\r\n　　　　　　　　　　　　this.list.splice(i, 1)\r\n　　　　　　　　　　　　//在数组的some方法中，如果return true，就会立即终止这个数组的后续循环\r\n　　　　　　　　　　　　return true\r\n　　　　　　　　　　}\r\n　　　　　　　　})　　　　　　\r\n　　　　　　},\r\n　　　　　　del(id){\r\n　　　　　　　　var index = this.list.findIndex(item =&gt;{\r\n　　　　　　　　　　if(item.id==id){\r\n　　　　　　　　　　　　return true\r\n　　　　　　　　　　}\r\n　　　　　　　　})\r\n　　　　　　　　this.list.splice(index,1)\r\n　　　　　　}\r\n　　　　}\r\n　　})\r\n&lt;/script&gt;</code></pre>', '//htmldiv id=\"app\"//v-for循环就不写了 每一条数据最后都有一个删除的超链 .prevent阻止默认的跳转行为　只执行点击事件a href=\"…', '2019-09-12 14:42:47');
INSERT INTO `bbs_questions` VALUES ('ad6b9556-efef-468b-95dc-7d3a21441619', '1ba2b9fc-aed6-452d-91df-36e3afada4f5', '111111111111linux', '1568274694176', 'effcee5a-cb08-4221-8a6b-d2eb94c3a93c', 'linux', 0, 4, 0, 0, 1, b'0', b'1', '<p><span style=\"font-family: Monaco;\"> <span style=\"color: #e5da73;\">linux</span></span>&nbsp;<span style=\"color: #e5da73; font-family: Monaco;\">linux</span><span style=\"color: #e5da73; font-family: Monaco;\">linux</span><span style=\"color: #e5da73; font-family: Monaco;\">linux</span>&nbsp;</p>', ' linux linuxlinuxlinux ', '2019-09-12 15:51:34');
INSERT INTO `bbs_questions` VALUES ('b8d856ce-fbb2-43e1-8829-65de9d4873b2', '1ba2b9fc-aed6-452d-91df-36e3afada4f5', '11111111', '1568270742056', 'effcee5a-cb08-4221-8a6b-d2eb94c3a93c', 'vue', 0, 50, 0, 0, 1, b'0', b'1', '<pre class=\"language-javascript\"><code>//html\r\n&lt;div id=\"app\"&gt;\r\n　　//v-for循环就不写了 每一条数据最后都有一个删除的超链 .prevent阻止默认的跳转行为　只执行点击事件\r\n　　&lt;a href=\"\" @click.prevent=\"del(item.id)\"&gt;删除&lt;/a&gt;\r\n&lt;/div&gt;\r\n\r\n//script\r\n&lt;script&gt;\r\n　　var vm = new Vue({\r\n　　　　el:\'app\',\r\n　　　　data:{\r\n　　　　　　id:\'\',\r\n　　　　　　name:\'\',\r\n　　　　　　list:[\r\n　　　　　　　　{id:1, name:\'惊鲵\'}，\r\n　　　　　　　　{id:2, name:\'掩日\'}，\r\n　　　　　　　　{id:2, name:\'黑白玄翦\'}\r\n　　　　　　]\r\n　　　　},\r\n　　　　methods:{//methods中定义了当前vue实例中所有可用的方法\r\n　　　　　　del(id){\r\n　　　　　　　　this.list.some((item, i)=&gt;{\r\n　　　　　　　　　　if(item.id==id){\r\n　　　　　　　　　　　　this.list.splice(i, 1)\r\n　　　　　　　　　　　　//在数组的some方法中，如果return true，就会立即终止这个数组的后续循环\r\n　　　　　　　　　　　　return true\r\n　　　　　　　　　　}\r\n　　　　　　　　})　　　　　　\r\n　　　　　　},\r\n　　　　　　del(id){\r\n　　　　　　　　var index = this.list.findIndex(item =&gt;{\r\n　　　　　　　　　　if(item.id==id){\r\n　　　　　　　　　　　　return true\r\n　　　　　　　　　　}\r\n　　　　　　　　})\r\n　　　　　　　　this.list.splice(index,1)\r\n　　　　　　}\r\n　　　　}\r\n　　})\r\n&lt;/script&gt;</code></pre>\r\n<p>aa</p>', '//htmldiv id=\"app\"//v-for循环就不写了 每一条数据最后都有一个删除的超链 .prevent阻止默认的跳转行为　只执行点击事件a href=\"…', '2019-09-12 14:45:42');

