-- ----------------------------
-- Table structure for cms_article
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article`  (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自动增长',
  `ColumnId` int(11) NOT NULL DEFAULT 0 COMMENT '栏目ID',
  `Types` int(11) NOT NULL DEFAULT 0 COMMENT '0=新闻1=多图片',
  `Title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `TitleColor` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章标题颜色',
  `SubTitle` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章副标题',
  `Author` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者',
  `Source` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '来源',
  `IsLink` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否外链',
  `LinkUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '外部链接地址',
  `Tag` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章标签',
  `Sort` int(11) NOT NULL DEFAULT 1 COMMENT '排序',
  `ImgUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章宣传图',
  `ThumImg` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章缩略图',
  `VideoUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '视频链接地址',
  `IsTop` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否置顶',
  `IsHot` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否热点',
  `IsScroll` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否滚动',
  `IsSlide` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否幻灯',
  `IsComment` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否允许评论',
  `IsWap` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否手机站显示',
  `IsRecyc` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否在回收站',
  `Audit` bit(1) NOT NULL DEFAULT b'1' COMMENT '审核状态',
  `KeyWord` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Seo关键字',
  `Summary` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章摘要',
  `Content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容',
  `Hits` int(11) NOT NULL DEFAULT 0 COMMENT '点击量',
  `DayHits` int(11) NOT NULL DEFAULT 0 COMMENT '当日点击量',
  `WeedHits` int(11) NOT NULL DEFAULT 0 COMMENT '星期点击量',
  `MonthHits` int(11) NOT NULL DEFAULT 0 COMMENT '月点击量',
  `LastHitDate` datetime(0) NULL DEFAULT NULL COMMENT '最后点击时间',
  `EditDate` datetime(0) NULL DEFAULT NULL COMMENT '编辑时间',
  `AddDate` datetime(0) NULL DEFAULT NULL COMMENT '添加时间',
  `DelDate` datetime(0) NULL DEFAULT NULL COMMENT '删除到回收站时间',
  `SiteGuid` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`Id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 1432 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of cms_article
-- ----------------------------
INSERT INTO `cms_article` VALUES (1008, 1023, 0, '祝贺飞易腾网站改版成功', NULL, NULL, '管理员', NULL, b'0', NULL, NULL, 1, NULL, NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, NULL, '<p>祝贺飞易腾网站改版成功！飞易腾公司是北京地区专业做网站的公司.自创立以来一直致力于网络品牌规划、品牌网站建设、互动营销执行，成为互动网站建设的先行者，坚持网站与创意结合，网站与行业结合，网站与营销结合，先进技术与传统模式的融合。为政府事业单位、国企、电子商务企业、传统企业等各级客户提供切合实际的互联网应用解决方案。我们不只是希望与您建立客户关系，更希望用我们的网络技术所长与您建立长期的战略合作伙伴关系，共同发展。</p><p>我们的核心业务：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;企业网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;行业门户网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;电子商务网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;政府事业单位网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;协会组织网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;分类信息网站策划与建设</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;团购网站策划与建设</p><p>我们的优势：</p><p>1、网站管理系统自主知识产权</p><p>2、网页设计符合国际W3C标准</p><p>3、整站框架符合SEO优化标准</p><p>&nbsp;</p><p>我们的态度：提供全面的网站应用解决方案，成为客户的网站应用顾问</p><p>我们的作品：三和连锁酒店&nbsp;、北京福田专用车、北京紫气东方、中国石材网、芒果票务网、万享通、你好网等上百家案例，涉及中小型企业、行业门户网站、电子商务网站等。</p><p>飞易腾承诺：用我们多年的成功经验，为您提供专业的网站建设服务。</p><p><br/></p>', 466, 153, 156, 440, '2018-12-12 07:48:12', '2013-03-25 10:25:23', '2016-11-21 10:27:52', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1009, 1023, 0, '百度绿萝算法上线,飞易腾科技持续跟进', NULL, NULL, '管理员', NULL, b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/Lvluo1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, NULL, '<p>北京网站建设_网页设计_北京网站制作-飞易腾科技专业开发，跟进技术潮流，主要研究互联网开发技术、页面设计规范、搜索引擎算法更新等，一下为百度绿萝算法上线公告。</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/Lvluo2.jpg\" title=\"1-14101914120H23.jpg\" alt=\"1-14101914120H23.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p>亲爱的站长朋友们：</p><p>&nbsp;大家好！</p><p>&nbsp;三个多月前，我们与各位站长朋友分享了百度对超链作弊的态度，经过一段时间的观察和数据积累，我们很欣喜地看到部分网站已经停止了这种行为并逐步的清除掉了此前的作弊超链，这对我们互联网生态环境正向发展是十分难能可贵的；但与此同时，我们仍然痛心的发现，反复强调过的买卖链接行为仍然大量存在，在此，我们再一次提醒这部分站长，此类链接早已被系统所识别并且已经从链接计算中清除。</p><p>&nbsp;我们有必要再次强调：买卖链接行为一方面影响用户体验，干扰搜索引擎算法；另一方面投机建站者得利，超链中介者得利，真正勤勤恳恳做好站的站长却在这种恶劣的互联网超链环境中无法获得应有的回报。因此针对买卖链接行为在清除外链计算的基础上，我们将对站点本身作出进一步调整。</p><p>此次调整中，以下三个类型网站将会受到不同程度的影响：</p><p>1、超链中介</p><p>&nbsp;超链本应是互联网上相对优质的推荐，是普通用户及网站之间对页面内容、网站价值的肯定，但是现在种种超链作弊行为使得真实的肯定变成了一些人谋取利益的垫脚石，用户无法根据链接的推荐找到需要的优质资源，并且严重干扰搜索引擎对网站的评价。超链中介便是这畸形的超链市场下形成的恶之花，我们有义务维护超链的纯净维护用户利益，也有责任引导站长朋友们不再支出无谓的花销，所以超链中介将在我们的目标范围内。</p><p>2、出卖链接的网站</p><p>&nbsp;一个站点有许多种盈利方式，利用优质的原创内容吸引固定用户，引进优质广告资源，甚至举办线下活动，这些盈利方式都是我们乐于见到的，是一个网站的真正价值所在。但是一些网站内容基本采集自网络，以出卖超链位置为生；一些机构类网站或被链接中介所租用进行链接位置出售，使得超链市场泡沫越吹越多。此次的调整对这类站点同样将有所影响。</p><p>3、购买链接的网站</p><p>&nbsp;一直以来，百度对优质站点都会加以保护和扶植，这是从用户需求以及创业站长的角度出发的必然结果。而部分站长不将精力用在提升网站质量上，而选择钻营取巧，以金钱换取超链，欺骗搜索引擎进而欺骗用户。对于没有太多资源和金钱用于此类开销的创业站长来说，也是一种无形的伤害，如果不进行遏制，劣币驱逐良币，势必导致互联网环境愈加恶劣。此次调整这类站点本身也将受到影响。</p><p>&nbsp;最后，感谢专心做好站的站长们的支持，也感谢用户多年的使用，这些就是我们一直努力净化互联网环境的动力。请仍身处超链买卖漩涡中的站长朋友们尽快停止这种无效的行为，并且认识到优质的原创内容，独一无二的服务，为用户着想的信息组织，才是一个网站逐步成长的正确道路。今后我们也会从这些角度出发，继续为用户提供便捷的服务，为更多站长带来更丰厚的收益。</p><p><br/></p>', 480, 175, 177, 454, '2018-12-14 15:35:20', '2013-04-01 10:27:56', '2017-09-12 17:17:48', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1010, 1023, 0, '北京飞易腾开发完成誉龙如意APP商城', NULL, NULL, '管理员', NULL, b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/yulongruyi1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '北京飞易腾开发完成誉龙如意商城', '<p>誉龙如意APP商城随时随地可以享受优惠。誉龙如意APP商城可以商家入驻，已经入驻商家有雀巢、TCL王牌、华为、晨光、水星家纺、Dior、SONY等。积分商城里的商品需要积分也需要支付钱，价钱会比没有积分的价钱低。还有购物车、个人中心、如意算、分类专区等功能点。</p><p>誉龙如意APP商城二期将要开发誉龙超市、粉丝扒、誉龙直播等功能。敬请期待！<br/></p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/yulongruyi2.jpg\" title=\"启动页3.jpg\" alt=\"启动页3.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p>', 675, 300, 307, 652, '2018-12-12 16:22:20', '2017-02-10 15:27:52', '2017-09-12 10:59:14', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1011, 1023, 0, '关于响应式页面设计', NULL, '关于响应式页面设计', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/xiangyingsYMSJ1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '无线网络成为了现在的主流，WAP应用越来越广泛，很多企业在进行着网站升级开发-WAP网站的制作。飞易腾科技借用迅雷的文章介绍一下“关于响应式页面的设计”。', '<p>无线网络成为了现在的主流，WAP应用越来越广泛，很多企业在进行着网站升级开发-WAP网站的制作。飞易腾科技借用迅雷的文章介绍一下“关于响应式页面的设计”。</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/xiangyingsYMSJ2.jpg\" title=\"1361428277324.jpg\" alt=\"1361428277324.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p><br/></p><p>作为一个无线部门的人，不懂移动设备是不行的。而作为一个无线的重构，不会写响应式页面更是不行得。而我，一个无线的重构，在我最近做的一个移动端的项目之前，的确是不会写响应式页面的，所以，严格来说，在这个项目之前，我是一个不合格的无线重构人。</p><p>而这个项目，却让我快速地掌握了响应式页面重构的一些方法。下面就是通过这个项目来总结我在响应式页面重构学到的东西。</p><p>众所周知，所谓响应式页面，就是能够用一套样式，使你的页面能够在不同分辨率的屏幕下都有很好的表现形式。响应式Web设计，这个概念是Ethan Marctte 在A List Apart 发表的一篇文章“Responsive Web Design”中援引响应式建筑而得名：</p><p>响应式建筑（responsive architecture），物理空间应该可以根据存在于其中的人的情况进行相应。</p><p>根据我所阅读过一些文章及资料，我总结出响应式页面的几个关键组成部分：</p><p>1、页面头部的meta说明，可以通过viewport meta标签去让你的html页面的的宽度能根据设备分辨率让浏览器的可视宽度来适应，也可以在这里设置页面的缩放比例等等，这样在成比例的分辨率设备下，就可以更简单地实现响应式。</p><p>&lt;meta name=“viewport” content=“width=device-width， initial-scale=1.0”&gt;</p><p>2、流体布局（fluid grid），所谓的流体布局，其实就是在你pc端实现的页面基础上，将一些元素的宽高由原来的固定多少像素（px）调整为百分比（%）或字体比例（em）（或布局方面的margin、padding、left、top等以px为单位的值），这也是当前实现响应式布局的两种主要实现方法。</p><p>第一种用百分比（%），就是以该元素的父容器的宽高为100%，其他元素的宽高相对于其父容器的比例，只要将具体的像素值相对于他的父容器的一个百分比折算即可。当然这种方法的换算有点复杂，因为很多相对的宽高折算的百分比系数是带小数的，所以这时候可能要你有足够的耐心才能实现。</p><p>在Ethan Marctte的Responsive Web Design这篇文章中给出的一个demo中，我们可以看到他的实际代码里：</p><p>@media screen and （max-width： 400px） {</p><p>.figure，</p><p>li#f-mycroft {</p><p>margin-right： 3.317535545023696682%; /* 21px / 633px */</p><p>width： 48.341232227488151658%; /* 306px / 633px */ }</p><p>第二种方法是用字号比例（em）去实现，其实方法是跟上面一样的，只不过我们将%换成了em，这种方法就是某元素具体的宽高（px）在当前基准字号（font-size）下折算出多少个em。eg： 一个在480分辨率下宽高为64px*64px的元素，其父容器的字号（font-size）为20px，那么它折算成em为单位就是3.2em*3.2em。当其父容器字号基准根据不同的分辨率变化的时候，该元素的宽高也能根据这个字号基准成比例的缩放，就能实现响应式变化。</p><p style=\"text-align: center;\"><img src=\"/upload/image/20161222/6361801474890450431886576.jpg\" title=\"1361428277772.jpg\" alt=\"1361428277772.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/xiangyingsYMSJ3.jpg\" title=\"1361428277156.jpg\" alt=\"1361428277156.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p><br/></p><p><br/></p><p>从上面的两张实例图我们可以看到，同一个元素，宽高为3.2em*3.2em，在360px分辨率下，因为基准字号为15px，故解析出来的实际尺寸为48px*48px，而在480px分辨率下，基准字号为20px，故实际的尺寸为64px*64px。</p><p>3、流体图片（liquid image），在我所了解的很多资料中，对图片处理这块，如果要使图片能根据分辨率来适应，而且还不失真，好像挺困难的。但其实我们不用考虑的那么复杂，我们要做的只是让图片能根据不同分辨率自适应罢了，我们不管图片会不会因为被拉伸而失真，因为真的遇到这样的情况，我们可以考虑在不同分辨率下使用不同的图片，这样就简单多了。所以让图片尺寸自适应，我们只要不给图片设定具体的宽高尺寸，只要在样式中给该图片一个width：100%，这样图片就能根据它父容器的尺寸自动调整了。</p><p>4、媒体查询（media query），这个也是响应式页面的一个关键技术，根据不同的分辨率去调整一些不同的样式。</p><p>@media screen and （max-device-width： 480px） {</p><p>.column {</p><p>float:none;</p><p>}</p><p>}</p><p>通过上面的这样媒体查询结构，我们可以设定在不同分辨率下选用不同的样式来调整响应式页面。像前面第二点流体布局上，我们使用百分比或字号比例去实现流体布局的时候，第一种方法是可以不用媒体查询直接实现流体布局的，就是元素的宽高能自适应不同分辨率屏幕。</p><p>但第二种方法用字号比例（em）去实现流体布局的时候，我们就必须要结合媒体查询了，因为我们的字号比例是根据基准字号来实现的，就是说在基准字号一定的情况下，该元素的大小就是固定的，而我们要实现该元素尺寸自适应，就只能通过调整基准字号来实现了。通过媒体查询，我们可以让在基准字号font-size在不同分辨率下不一样，这样其子元素相对于该字号的比例em算出来的像素px就不一样了，这样就能实现响应式了。</p><p>所以我们兼容不同的分辨率的时候，可以先在某个分辨率下，实现完美的重构，然后将所有元素具体的尺寸（px）折算为em（根据父容器的font-size），然后再通过媒体查询，调整不同分辨率下的基准字号font-size就能实现具体的响应式了。</p><p>当然媒体查询的功能是根据不同的分辨率适配不同的样式，我们可以通过上面的做法是实现流体布局，还可以通过媒体查询来细调具体的页面在不同分辨率下的不同表现形式。</p><p>在我具体的项目过程中，采用媒体查询主要是调整不同分辨率下的基准字号的大小，具体如下面的代码所示：</p><p>body，section，button，h1，p，.layer，.downall_btn，.introduce，.playlist，.recom_picbox{font-size:20px;}</p><p>/* for 800 px width screen */</p><p>@media only screen and （max-device-width:800px），only screen and （max-width:800px）{</p><p>body，section，button，h1，p，.layer，.downall_btn，.introduce，.playlist，.recom_picbox{font-size:33.34px;}</p><p>}</p><p>/* for 720 px width screen */</p><p>@media only screen and （max-device-width:720px），only screen and （max-width:720px）{</p><p>body，section，button，h1，p，.layer，.downall_btn，.introduce，.playlist，.recom_picbox{font-size:30px;}</p><p>}</p><p>前面设置通用字号为20px，当分辨率超过我媒体查询到的最大屏幕宽度的时候就适配该基准字号，下面分别通过媒体查询为分辨率为800px和720px的设备详细调整基准字号，（当然在这里你可以增加更多的样式去调整不同分辨率下的具体表现）使页面在两个分辨率下都能有比较好的表现。可以发现，在800px的分辨率设备中，我的基准字号设置为33.34px，在720px的分辨率设备下的基准字号为30px。</p><p>原文链接：http://cued.xunlei.com/log057</p><p>为什么在800px分辨率下基准字号就为33.34px，在720px分辨率下基准字号为30px呢，这是因为我是先以480px分辨率下基准字号为20px去实现的，那么在800px或720px下的基准字号就根据设备分辨率的比例去算出。 这里只给出两个分辨率的例子，其他不同分辨率下的实现方法一样。</p><p>通过上面的几点关键技术，我们就可以实现具体的响应式页面了。看完这篇文章，是不是觉得响应式页面其实也没有想象中那么难呢？那么，有空自己去试试吧，只有自己动手实现后才能真正理解其中的奥秘哦！</p><p><br/></p>', 440, 133, 138, 417, '2018-12-15 12:50:10', '2016-12-22 14:38:12', '2017-09-12 17:12:56', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1012, 1023, 0, '飞易腾分享之“内容营销”', NULL, '飞易腾分享之“内容营销”', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/neirongyingx1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, 'SEO优化推广进入白热化阶段，网站优化推广中有一句话是“内容为王，外链为皇”，可知网站的内容在SEO优化推广中所占的地位，所以在网站优化推广中狠抓原创文章，搞些文章创意也成为了重点，电视选秀类节目可以给内容营销运营者们一些有益的启示。', '<p>SEO优化推广进入白热化阶段，网站优化推广中有一句话是“内容为王，外链为皇”，可知网站的内容在SEO优化推广中所占的地位，所以在网站优化推广中狠抓原创文章，搞些文章创意也成为了重点，电视选秀类节目可以给内容营销运营者们一些有益的启示。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/neirongyingx2.jpg\" title=\"15497089590130335771.jpg\" alt=\"15497089590130335771.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p style=\"text-align: center;\">直通春晚最终获胜者阿普萨萨(网络图片)</p><p>他们强调：“那些最终成为(选秀节目)决赛选手的人，就是那些‘知道他们自己是谁’的人”、“(无论如何，)你的内容都不应该与竞争者的一样，或者跟别的人一样，它应当听起来就是你的”。</p><p>言下之意很明显，现在许多的“内容营销”实施者，并不“知道自己是谁”，也不知道自己内容营销要达到什么样的目的，通过如何的手段来达到，因此不可能真正做好“内容营销”工作的。</p><p>的确，在选秀节日中，“那些最终成为(选秀节目)决赛选手的人”，不但知道“自己拥有什么”，更知道“自己想要成为什么样的人”。</p><p>也许绝大多数“最终成为决赛选手的人”，在某一专业(如歌唱表演)有一定天赋，而且热爱这一专业(如歌唱表演)——但仅此还远远不够，为此，他们几十年如一日地坚持自己的爱好，并自觉通过长年不懈坚持与有意识学习训练，不断地提升自己，使得自己的天赋爱好得到更好地发挥，形成自己拿得出手的一门绝活，并在一个较小的范围内拥有一定知名度——“成为专家”。</p><p>其次，在此类选秀节日中“那些最终成为决赛选手的人”，他们知道每一次机会对于自己都十分的短暂宝贵——虽然目前国内外各种选秀节目多如牛毛，参加门槛极低，但对于他们来说，只有目前的机会对于自己才是最真实的机会，必须更好地把握，否则就意味着出局，无法在梦想的舞台上继续前进，实现自己“莫愁前路无知己，天下谁人不识君”的梦想。</p><p>因此，在有限的表演时间内，他们必须把最能体现自己风格(不仅仅是实力)的一面展现给评委、嘉宾及现场观众，无论是原创还是翻唱，无论是穿着还是装饰，无论是表演内容还是表演姿态，他们都“八仙过海，各显神通”，力求“细节致胜”，给观众全新感受，充分展示一个独特的自己。</p><p style=\"text-align: center;\"><img src=\"/upload/image/20161222/6361801542129653418372227.jpg\" title=\"001ve3i3zy6J4UFUhmK16&amp;690.jpg\" alt=\"001ve3i3zy6J4UFUhmK16&amp;690.jpg\"/></p><p><br/></p><p style=\"text-align: center;\">星光大道舞台上的“草帽姐”徐桂花(网络图片)</p><p>如《央视2012星光大道总决赛》中，家政小时工周艳以女声翻唱《船工号子》、《我的家我的天堂》;“草帽姐”徐桂花则那顶草帽始终不离头，都给人留下深刻印象;又比如《直通春晚》舞台上，独龙族歌手阿普萨萨以正宗的原创民族音乐《庆丰收》、《傈僳欢歌》技惊四座。但为展示自己全面的音乐才能，同时出于“好东西一次不能给太多”的营销考虑，他有意识地演唱了《巴山背二哥》、《父亲的草原母亲的河》等一系列更为人广泛传唱，容易引起受众共鸣的歌曲。</p><p>并且，每个参加“选秀节目”的选手都希望，每一场比赛自己都能获胜，走得更远——但“那些最终成为决赛选手的人”更知道，要真正达到这个目的，走得更远，就必须忘记获胜，忘记一切营销目的，用自己投入忘我的歌唱表演说话，与评委、嘉宾及观众沟通交流，让自己的表演引起评委、嘉宾及观众的共鸣，最终对自己留下独特而深刻的印象。</p><p>在《内容营销》中，Ann Handley 与C.C.Chapman说：“和许多真人秀节目的环选手一样，你的内容必须有一种格调，能够立刻表现出你公司的独特性以及你的独特观点”。</p><p>这第一步，就是你必须“知道自己是谁”，现在“定位理论”很时髦，但想真正做好一个产品服务(或企业)的“定位”，首先就必须“知道自己是谁”，必须知道自己产品服务(或企业)的优势、劣势是什么，并知道自己内容营销要达到的目的，并不断地探索如何以更好的方式来达到自己的目的。</p><p>也许上面所举走到最后决赛的选手，身后都有专业营销团队策划包装，可这更证明了“知道自己是谁”定位之重要性，作为没有专业策划包装的普通内容营销者，就更必须“知道自己是谁”——知道自己营销目标是什么，知道自己的营销优势在哪里，不断探索展示自己营销优势的最佳表现手段，不能满足于随大流“你有我有全都有”的“复制采集伪原创”。</p><p>《内容营销》作者引用内容营销倡导者乔·普立兹的话说：“在你真正了解客户之前，必须了解你自己”——几千年前的《孙子兵法》也说“知己知彼，百战不殆”。</p><p>但遗憾的是，更多内容营销者并不知道营销目标是什么，也不知道自己营销对象的优势——“卖点” 在哪里，更不会琢磨用什么方式表达自己的独特卖点效果更好，只知道“复制采集伪原创”，创造更多内容，让百度更多收录，只想着百度搜索排行榜上更多“总在最前”的关键词排名——他们甚至不关心越来越高的内容制作成本与越来越低的潜在用户持续注意力。只知道“唯百度马首是瞻”，不断地“围绕关键词做文章”，抱着这样的心态想要做好内容营销，简直比登天还难。</p><p>要知道，现今网络内容呈爆炸式增长，每天数以千万的新网页面世，24小时工作的第二代搜索引擎都早已疲于奔命，更甭说“仗越打越精”的网友了，他们即使打开一个网页，也仅仅有短短几十秒的关注时间，你如果没有周艳、徐桂花、阿普萨萨等人“把握眼前机会”的迫切感，并不断研究自己内容营销的最佳表现手法，在有限时间内用自己独特的内容表现形式打动潜在用户——形成自己让所有人眼前一亮的独特风格，想要内容营销成功，你永远都只会是“明日复明日，明日何其多”的“等明天”黄粱美梦罢了。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/upload/image/20161222/6361801543847981117687102.jpg\" title=\"1351239095744_1351239095744_r.jpg\" alt=\"1351239095744_1351239095744_r.jpg\"/></p><p style=\"text-align: center;\">周艳——首位走上星光大道总决赛舞台的家政小时工(网络图片)</p><p>总之，“那些最终成为(选秀节目)决赛选手的人”告诉我们，重要的不是一时营销数据，而是要通过“知道自己是谁”、“我想要告诉用户我是谁”的准确定位并与之相适应的整体内容策略，打造营销对象独特的风格，让人印象深刻，过目难忘——想想“草帽姐”、“星光大道的家政小时工”、“独龙族(全国人口最少民族之一)唯一的专业音乐人”称谓后蕴藏力量吧。</p><p>在书中，Ann Handley 与C.C.Chapman强调：“在展示给力的内容时一个关键的步骤，就是展示你自己独特的风格(，在极为有限的表现时间里，以自己独具匠心的表演，打动自己要征服的对象，产生共鸣)”。</p><p>“那些最终成为(选秀节目)决赛选手的人”给内容营销者的最后一个启示是：要善于利用各种现有的“行业活动”展示自己，不断地提高自己业界知名度与用户认可度。</p><p>这一点大家都知道，但“那些最终成为(选秀节目)决赛选手的人”之成功证明，只有真正“知道自己是谁”、“我想要让用户知道我是谁”的人，才会真正明白自己应该选择什么样的行业活动，并懂得在行业活动中应该如何展示自己，才可能真正地达到自己的营销目标</p><p><br/></p>', 416, 122, 125, 393, '2018-12-13 23:54:03', '2016-12-22 14:46:50', '2017-09-12 17:04:04', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1013, 1023, 0, '热烈祝贺飞易腾签约黎明文仪家具商城网站建设', NULL, '热烈祝贺飞易腾签约黎明文仪家具商城网站建设', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-LMWYI1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '北京黎明文仪家具有限公司创建于1993年，经过十多年的创业与拼搏，公司规模及经营范围逐步扩大。现已发展成为生产民用家具、办公家具、酒店家具、学生公寓家具、实木门及人造板等六大系列2000多种产品，集科、工、贸于一体的集团企业。', '<p>热烈祝贺飞易腾签约黎明文仪家具商城网站建设！</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-LMWYI2.jpg\" title=\"130706150020559765.jpg\" alt=\"130706150020559765.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p>北京黎明文仪家具有限公司创建于1993年，经过十多年的创业与拼搏，公司规模及经营范围逐步扩大。现已发展成为生产民用家具、办公家具、酒店家具、学生公寓家具、实木门及人造板等六大系列2000多种产品，集科、工、贸于一体的集团企业。年产值达5亿元，是北京市家具行业最具影响力企业之一。&nbsp;<br/>公司位于北京市通州区中关村科技园区通州园金桥科技产业基地景盛南四街18号，占地120000平方米，厂房面积100000平方米。注册资金10100万人民币。拥有员工1600多人，其中一大批为企业管理、科技开发、市场营销方面的人才。公司从德国、意大利、芬兰引进了代表国际先进水平的木工机械设备。最具代表性的有德国HOMAG双端封边机、意大利EB120电子开料锯、德国HPP11/38电子开料锯、意大利CNC加工中心、意大利CEFCA-UV辊涂自动油漆生产线、意大利CEFCA全自动封闭喷漆生产线、芬兰进口中央吸尘设备。公司还拥有从原材料到成品的质量检测中心，使黎明家具产品品质有绝对保障。<br/>黎明家具于2002年通过了ISO9001：2008质量管理体系认证，2003年通过了ISO14001：2004环境管理体系认证，2005年公司资信等级评为AAA级，2007年通过了中国环境标志产品认证证书，2009年通过了职业健康安全管理体系认证证书，2011年通过了方圆标志产品认证证书。十几年来，黎明家具及其产品屡获殊荣，自2000年起年年被北京市工商局公示为守信企业，是“中国著名品牌”重点推广单位。2005年被确定为中央国家机关政府采购定点供应商。黎明家具为中共中央办公厅、国家广电总局、北京市人民政府等党政机关，为中科院、中国航天科技集团、清华大学等科研单位，为联想集团、大唐电信、中国石化工程公司、建设银行北京市分行等大型企业提供了各类家具及配套服务，并受到社会一致好评。&nbsp;</p><p>飞易腾公司是北京地区专业网站策划、网站建设的互联网应用开发公司，鉴于电子商城网站建设方面的优势顺利签约黎明文仪家具公司。</p><p><br/></p>', 452, 155, 159, 429, '2018-12-15 06:37:35', '2016-12-22 16:03:07', '2017-09-12 17:00:56', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1014, 1023, 0, '热烈祝贺飞易腾签约果客网培训网站建设', NULL, '果客网培训网站建设', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, '/upload/image/20161222/6361801996625035055148839.gif', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '果客网是北京东方神驰科技发展有限公司旗下，专业进行移动互联网技术开发和培训的3G研发培训中心，是国内最具技术实力的移动互联跨平台研发中心。', '<p>热烈祝贺飞易腾签约果客网培训网站建设！</p><p style=\"text-align: center;\"><img src=\"/upload/image/20161222/6361801996625035055148839.gif\" title=\"banner4.gif\" alt=\"banner4.gif\"/></p><p>果客网是北京东方神驰科技发展有限公司旗下，专业进行移动互联网技术开发和培训的3G研发培训中心，是国内最具技术实力的移动互联跨平台研发中心。我们在iOS、Android、Windows Phone、Embedded Linux等多个3G移动系统平台上有着众多的项目积累和骄人业绩，研发的产品涉及行业应用、电信运营、游戏娱乐、教育培训、生活服务等不同领域，并以先进的技术、优质的服务，获得了客户和最终用户的认可。&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如今，我们以专业的技术实力和强大的研发团队，与中科院、霍尼韦尔及法国电信等众多世界500强企业合作，为他们提供软件开发、产品定制、系统设计和咨询服务等广泛内容。</p><p>飞易腾作为专业的网站建设服务商，为果客网在IOS培训行业树立企业品牌，为果客网飞腾发展保驾护航。</p><p><br/></p>', 407, 113, 118, 384, '2018-12-12 11:28:58', '2016-12-22 16:09:42', '2016-12-22 16:13:03', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1015, 1023, 0, '热烈祝贺飞易腾签约河北北方学院农林科技学院网站制作项目', NULL, '签约河北北方学院农林科技学院网站制作项目', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-HBBFXY1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, ' 热烈祝贺飞易腾签约河北北方学院农林科技学院！飞易腾为农林科技学院进行网站策划、网站制作等网站建设综合项目，在大学院校网站建设中再创新业绩。\r\n    农林科技学院于2005年10月由原河北北方学院农业科学系，食品科学系，园艺系组建而成。其前身张家口农业高等专科学校农业系，创建于1923年，至今已有近90年的办学历史。2009年被河北省农业厅授予“现代农业技术培训基地”称号；2010年被国家农业部授予“农业部现代农业技术培训基地”称号。 ', '<p style=\"text-align: center;\">&nbsp; &nbsp;<img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-HBBFXY2.jpg\" title=\"农科院.jpg\" alt=\"农科院.jpg\" width=\"500\" height=\"161\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 161px;\"/></p><p>&nbsp;热烈祝贺飞易腾签约河北北方学院农林科技学院！飞易腾为农林科技学院进行网站策划、网站制作等网站建设综合项目，在大学院校网站建设中再创新业绩。</p><p>&nbsp; &nbsp;&nbsp;农林科技学院于2005年10月由原河北北方学院农业科学系，食品科学系，园艺系组建而成。其前身张家口农业高等专科学校农业系，创建于1923年，至今已有近90年的办学历史。2009年被河北省农业厅授予“现代农业技术培训基地”称号；2010年被国家农业部授予“农业部现代农业技术培训基地”称号。&nbsp;<br/>学院现有教职工105人，其中正高13人，副高37人；专任教师中博士14人，硕士67人，硕士以上学历教师约占专任教师的90%，另有兼职教授9人。专任教师中享受省中青年骨干教师津贴2人，省优秀教师2人，市级拔尖人才2人，在校生2400多人。&nbsp;<br/>在校生2672人，其中本科生1550人，专科生1122人。下设有办公室、教学科研科、学生管理科、团委党政科室，有农业科学系、植物科学系、植物保护系、园艺系、食品科学系、实验中心和9个教研室，18个实验室。学院现有农学、植物科学与技术、种子科学与工程、食品科学与工程、植物保护、，园艺6本科专业；作物生产与技术，生物技术及应用，植物保护，种子生产与经营，食品生物技术，食品加工与技术，食品营养与检测，园艺技术，园林技术，设施农业技术10个专科专业。学院拥有总面积876m2花卉温室，食用菌实验基地，有占地面积200多亩的2个校内实验农场， 1个食品科学研究所；另有24个本科校外生产实习基地，与省内外百余家企业建立了合作伙伴关系。近年来学院十分重视学科建设和硬件投入，教学实验和科研条件有了较大改善。现有3门课程被评为校级优秀课程。? 其中“作物栽培学与耕作学”被评为校级重点学科；农学专业被河北省评为特色专业和创新高地。</p><p>学院十分重视科研和为地方经济服务，近年来主持、主研各级各类科研项目80多项，获得省长特别奖、科技进步奖、教学成果奖等各级奖项60多项；发表论文500多篇。学院注重科研成果的转化应用，把为“三农”服务和社会主义新农村建设作为己任，坚持走产、学、研相结合的路子，为地方经济建设做出了巨大贡献。有多名科技人员受到国家、省、市表彰奖励。&nbsp;<br/>学院十分重视学生的实践动手能力的培养，毕业生普遍受到广大用人单位的好评，一次性就业率达90%以上。毕业生中有的成为著名的博导、硕导、教授等专家、学者，许多人是单位的技术骨干，有的已成为大企业的董事长和企业家，还有相当一部分毕业生走上了领导岗位。&nbsp;<br/>随着党中央对“三农”问题的高度重视，中国农业现代化建设和新农村建设需要有大批的农业科技人才，农林科技学院的任务将更加艰巨，发展前景将更加美好。</p><p><br/></p>', 401, 119, 123, 378, '2018-12-13 22:35:46', '2016-12-22 16:13:22', '2017-09-12 16:57:52', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1016, 1023, 0, '北京飞易腾网站建设设计工作的基本原则', NULL, '北京飞易腾网站建设设计工作的基本原则', '管理员', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-JBYZ1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '这个网络时代，大到门户网站、电商网站，小到企业站，WWW网站到处都是。网站建设的目的是为别人提供所需的信息，这样人家才会愿意光临，网站建设才有其真实意义，但有太多网站显然忘了这个目的，复杂的创意技巧跃居主角，内容信息反而沦为末端。不可否认，优异的网站建设比比皆是，但令人不予置评的网站还是到处都找得到。显然，不管是网站提供者还是广大的使用者，大家都迫切需要知道好的网站在哪里，或者符合所谓好网站的条件是什么？飞易腾认为以下几点供参考。', '<p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-JBYZ2.jpg\" title=\"banner01.jpg\" alt=\"banner01.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p>这个网络时代，大到门户网站、电商网站，小到企业站，WWW网站到处都是。网站建设的目的是为别人提供所需的信息，这样人家才会愿意光临，网站建设才有其真实意义，但有太多网站显然忘了这个目的，复杂的创意技巧跃居主角，内容信息反而沦为末端。不可否认，优异的网站建设比比皆是，但令人不予置评的网站还是到处都找得到。显然，不管是网站提供者还是广大的使用者，大家都迫切需要知道好的网站在哪里，或者符合所谓好网站的条件是什么？飞易腾认为以下几点供参考。</p><p>1.使用者优先的观念&nbsp;<br/>无论什么时候，不管是着手准备设计页面之前。 正在设计之中，或者已经设计完毕，有一个最高行动准则一定要牢记在心，那就是∶使用者优先。因为没有使用者去光顾， 任何自认再好的页面都是没有意义的。</p><p>2.考虑大多数人的连线状况</p><p>虽说现在网络提速了，但实际网络速度还是达不到发达国家水平那种的流畅程度，还有就是分电信、联通、移动三大运营商的网路。所以您在设计页面时就必需以这种普遍状况为设计参考量，尽量不放FLASH引导页，首页大图要进行压缩优化，不要放置一堆会加重塞车情形或让人等得发火的东西。</p><p>3.考虑使用者的浏览器</p><p>其次，网站的IE兼容性也是非常重要的，主流浏览器从火狐、谷歌，IE版本从IE7-IE9，，如果您想要让所有的人都可以毫无障碍地观看您的页面，那么最好使用所有浏览器都可以阅读的格式，不要使用只有部份浏览器可以支持的HTML格式或程序技巧。如果想要展现自己的高超技术又不想放弃一些潜在观众，可以考虑在主页中设置几种不同的观赏模式选项，供浏览者自行选择。</p><p>4.内容第一</p><p>“内容永远第一”，适合网络上的页面，而且几乎每个参与提供意见的人都举双手赞成这句话。没有内容的书本已经够令人讨厌， 但至少您还可以很快判断出来，而页面呢？您可能得耗费一整个晚上的时间与金钱，才会发现原来网站里根本没有您要找的东西，这种痛苦真不是三言两语可以形容。内容可以是任何东西，包括文字、图片、影像、声音等等，但请记住一点：一定要跟这个网站所要提供给人的讯息有关系。</p><p>5.着手规划、确定特色、锁定目标</p><p>不管做什么事，一定要进行规划，建置网站更是如此。规划时必须确定自己网站的性质、提供内容、目标观众，然后根据本身的软硬件条件来设置范围。网络的特色是及时、新鲜、丰富、热闹，这是吸引大家上网的条件，如果本身条件强大，可以根据上述原则使自己网站成为一个全方位的信息提供者，如果不足，就成为单方面的提供者；此外，还可以在特殊议题或主题上加以突出，进一步锁定目标观众。</p><p>6.第一页很重要</p><p>第一页是最重要的东西，因为它是别人认识这个网站的第一印象。如果是新开幕的站台，最好在第一页就对这个网站的性质与所提供内容做个扼要说明与导引，让别人判断要不要继续进入里面。最好第一页就有很清楚的类别选项，而且尽量符合人性化，让参观者可以很快找到需要的主题。在设计上，最好秉持干净而清爽的原则。第一、若无需要，尽量不要放置大型图形文件或加上不当的程序，因为它会增加下载时间，导致观赏者失去耐心； 第二、画面不要散置得太过杂乱无序，因为观赏者会找不到东西。</p><p>7.分类</p><p>内容的分类很重要，可以按主题分类、按性质分类、按机关组织分类，或按人类思考直觉式地分类等等，一般而言，按人类的直觉式思考会比较亲切。但无论哪一种分类方法，都要让使用者可以很容易找到目标。而且分类方法最好尽量保持一致，若要混用多种分类方法也要掌握不让使用者搞混的原则。此外，在每个分类选项的旁边或下一行，最好也加上这个选项内容的简要说明。</p><p>8.互动性</p><p>WWW的另一个特色就是互动。好的网站页面必须与使用者有良好的互动性，包括在整个设计呈现、使用介面导引上等等，都应该掌握互动的原则 ，让使用者感觉他的每一步都确实得到适当的回应，这部份需要一些设计上的技巧与软硬件支持。事实上，好的网站HomePage设计必须加上个人技巧、经验累积以及软硬件技术的配合运用等。</p><p>9.图形应用技巧</p><p>图形是WWW网站的特色之一，它带有醒目、吸引人以及传达讯息的功能，好的图形应用可以让页面增色不少 ，但不当的图形应用则会带来反效果，而其中又以大量使用无意义及大型的图形成为HomePage的败笔。笔者建议 ，如果可以，应尽量缩小或省略图形。在图形使用上，尽量采用一般浏览器均可支持的压缩图形格式，例如JPEG与GIF等，而其中JPEG的压缩效果较好，适合中大型的图形，可以节省传输时间。</p><p><br/></p>', 427, 122, 126, 404, '2018-12-13 22:23:59', '2016-12-22 16:54:55', '2017-09-12 16:48:04', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1017, 1023, 0, '热烈祝贺飞易腾签约大禹伟业(北京)国际科技有限公司网站制作项目', NULL, '大禹伟业(北京)国际科技有限公司网站制作项目', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/dayuweiye1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '热烈祝贺飞易腾签约大禹伟业(北京)国际科技有限公司网站制作项目', '<p>热烈祝贺飞易腾签约大禹伟业(北京)国际科技有限公司网站制作项目！经过飞易腾公司有效提供的网站策划案，最终签约大禹伟业中英文网站制作项目。</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/dayuweiye2.jpg\" title=\"7A57179A2C8B453CA3C5BD53724D5AA8.jpg\" alt=\"7A57179A2C8B453CA3C5BD53724D5AA8.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p>大禹伟业（北京）国际科技有限公司是一家集研发、生产,销售和施工的智能型高分子液体橡胶防水涂料和重防腐新型材料的高新技术企业。总部位于北京市海淀区，目前在新加坡、韩国和欧洲设有联络处，生产基地设在河北省三河市经济技术开发区，占地面积约12000㎡，山东菏泽，广东东莞设有分公司和制造基地。新加坡，韩国和欧洲设有办事处，工厂设在河北省三河市经济技术开发区，占地面积约12000㎡。公司已通过ISO9001质量管理体系、ISO14001环境管理体系认证。公司目前主要生产的具备全球领先水平且有自主知识产权的涂灵牌液体橡胶防水系列产品，将从根本上彻底解决边角、缝隙、不规则结构的连接处渗漏等技术难题，革新了传统防水材料的施工工艺和施工方法，开启了防水行业从“卷材时代”向“液体橡胶时代”过渡的新纪元，该产品目前已通过国家建设部科技成果评估，并被列为“建设部科技成果推广项目”。公司自成立以来，建立了底蕴深厚、特色鲜明的企业文化：以“创新、责任、环保”作为企业经营的核心价值观，以“做有责任心的防水和防腐行业进步依靠”为企业愿景，以“驱动技术进步，改善人居环境&quot;作为公司的使命，并最终成为“国际领先的防腐和防水行业系统解决方案服务提供商”。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>公司两大产品线可以广泛应用在铁路、地铁、轻轨、港口、桥梁、隧道、石油、市政、地下管网、能源和交通等领域；目前公司产品不仅应用在国家重点工程、大型市政工程等国家基础设施建设项目上，并且远销韩国、新加坡、巴西、印尼、马来西亚、日本和中东等国家和地区。</p><p>由于其突出的防腐和防水性能，公司产品荣获了包括“韩国总理奖”在内的十几项国际和国内奖项、专利和认证。</p><p><br/></p>', 416, 123, 124, 393, '2018-12-13 22:54:18', '2016-12-22 16:35:16', '2017-09-12 16:55:25', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1018, 1023, 0, '热烈祝贺飞易腾签约浩学文化油画作品相关网站制作', NULL, NULL, '网站策划师', '飞易腾', b'0', NULL, NULL, 1, NULL, NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '热烈祝贺飞易腾签约浩学文化油画作品相关网站制作！', '<p>热烈祝贺飞易腾签约浩学文化油画作品相关网站制作！</p><p>浩学文化中心是从事文化艺术创作、交流与经营的专业公司。主要从事当代艺术展示、研究、教育和收藏等为主要功能的复合型学术机构，中心在参与中国当代艺术生产与建构的同时，致力于在全球范围内推广及交流中国当代艺术和文化，并积极推介突出的国际艺术家和艺术项目...</p><p>&nbsp;</p><p><br/></p>', 461, 142, 143, 438, '2018-12-13 22:47:31', '2016-12-22 16:38:06', '2016-12-22 16:41:21', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1019, 1023, 0, '热烈祝贺飞易腾签约亚之星物流营销型网站制作', NULL, NULL, '网站策划师', '飞易腾', b'0', NULL, NULL, 1, '/upload/image/20161222/6361802208037742832162510.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '热烈祝贺飞易腾签约亚之星物流营销型网站制作！', '<p>热烈祝贺飞易腾签约亚之星物流营销型网站制作！</p><p style=\"text-align: center;\"><img src=\"/upload/image/20161222/6361802208037742832162510.jpg\" title=\"2013052710395798.jpg\" alt=\"2013052710395798.jpg\"/></p><p>北京亚之星物流有限公司是一家集信息咨询、物流服务、仓储配送、全程运输、电子商务结算为一体的现代化北京物流公司，有多年全国公路运输的专业成功经验。具有决对优势的运输价格，且已形成较为完善的运输网络。提供全方面“提---运---送”一站式门对门服务，可代收货款、运费到付、异地调货等服务。<br/>　　公司拥有一只素质良好，精干高效的专业运输队伍。管理层力求建立有特色的服务方式及企业文化，建立电子商务和网络化经营模式，实行标准化的业务流程，倡导“快速响应”的服务意识，发展一体化、多元化的运输服务体系。<br/>　　为了适应现代物流公司及快递发展的需要，更好的服务于新老客户，我们继往开来，以高标准、严要求来提高员工的素质，同时凭借先进的管理及专业的团队，构成了一个和谐的货运物流环境，打造一个崭新的物流企业。我们用自己的双手托起属于生产商与商户之间的桥梁，使您的商品物流八方。<br/>公司秉承“客户至上，锐意进取”的经营理念，“坚持客户第一”的原则为广大客户提供安全、经济、优质专业的服务，欢迎惠顾！</p><p>亚之星物流公司网站是在3年前制作的了，已经不能满足现阶段网站形象与网站推广的技术手段，为了更好的维护企业的形象以及企业信息化推广的需求，亚之星物流公司此次网站改版选择北京飞易腾科技有限公司，因为飞易腾公司是北京地区专业从事网站制作的公司，尤其是营销型网站、电子商务型网站、集团型网站、门户型网站等有独到的见解和实际的设计经验。</p><p>&nbsp;</p><p><br/></p>', 561, 219, 220, 538, '2018-12-12 06:16:54', '2016-12-22 16:42:19', '2016-12-22 16:48:39', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1020, 1023, 0, '热烈祝贺飞易腾签约传记中国-网站制作', NULL, '热烈祝贺飞易腾签约传记中国-网站制作', '网站策划师', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-chuanjiZG1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '热烈祝贺飞易腾签约传记中国-网站制作！传记中国网站是一家以回忆录为题材的网站，此网站充满着文化色彩。北京飞易腾公司经过专心策划研究，设计出充满着文化韵味的首页得到了客户好评。', '<p>热烈祝贺飞易腾签约传记中国-网站制作！传记中国网站是一家以回忆录为题材的网站，此网站充满着文化色彩。北京飞易腾公司经过专心策划研究，设计出充满着文化韵味的首页得到了客户好评。</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-chuanjiZG2.jpg\" title=\"2013042410454729.jpg\" alt=\"2013042410454729.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p><p>&nbsp; &nbsp;&nbsp;智慧“莫大于知来，来何以能知？据往事以为推而已矣”。<br/>&nbsp; &nbsp; 传记是一种文学形式。主要记述人和事，根据各种书面的、口述的回忆，调查相关材料，加以选择性的编排，描写与说明而成。传记和历史关系密切，某些写作年代久远的传记常被人们当史料看待。每个人都有属于自己的历史，这种个体化的历史常被总结成经验教训与人生观，潜移默化的传承给后人。传记中国以记录文化，传承历史为己任，真实性与文学性并重，书写人们的生活经历、精神风貌及其历史背景，创造属于每个鲜活人生的一家之言。“出乎史，如乎道，欲知大道，必先为史”。<br/>&nbsp; &nbsp; 传记中国的创作内容主要包括：企业史创作，回忆录创作以及成长奋斗史创作。<br/><br/><br/>企业使命： 记录文化，传承历史！<br/>创作理念： 遵循事实、精益求精！ 创作作风： 博学善用，融汇贯通！&nbsp;<br/>创作精神： 务实敬业，孜孜不倦！ 创作道德： 真诚、客观、执著！&nbsp;<br/>创作哲学： 求实，求真，求根本！ 创作宗旨： 传播人文之道，助力社会发展！&nbsp;</p><p><br/></p>', 405, 119, 123, 382, '2018-12-11 16:28:46', '2016-12-22 16:50:48', '2017-09-12 16:50:51', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1021, 1023, 0, '热烈祝贺飞易腾签约世纪爱晚门户网站制作项目', NULL, '热烈祝贺飞易腾签约世纪爱晚门户网站制作项目', '管理员', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-aiwan1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '热烈祝贺飞易腾签约世纪爱晚门户网站制作项目！\r\n世纪爱晚（海南）实业发展有限公司为专业的高端养生养老事业的运营机构，也是国家“爱晚工程”在海南高端养生养老产业发展的平台。公司以养生、养老、休闲、度假、旅游产业的投资、策划、管理、经营为己任，做精、做细、做大、做强中国的养生养老事业为理念，以实现国家“老有所养、老有所医、老有所教、老有所学、老有所为、老有所乐”的老年事业发展为目标。 ', '<p>热烈祝贺飞易腾签约世纪爱晚门户网站制作项目！</p><p>世纪爱晚（海南）实业发展有限公司为专业的高端养生养老事业的运营机构，也是国家“爱晚工程”在海南高端养生养老产业发展的平台。公司以养生、养老、休闲、度假、旅游产业的投资、策划、管理、经营为己任，做精、做细、做大、做强中国的养生养老事业为理念，以实现国家“老有所养、老有所医、老有所教、老有所学、老有所为、老有所乐”的老年事业发展为目标。&nbsp;<br/>公司遵照国家养老政策，在爱晚工程领导小组的指导下及爱晚工程国开行金融协调领导小组的金融支持下，运用自有资源优势，坚持科学发展观，创造社会经济效益，承担社会公益责任，积极推动“爱晚工程”在我国的全面发展。</p><p style=\"text-align: center;\">&nbsp;<img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-aiwan2.jpg\" title=\"2013051718050376.jpg\" alt=\"2013051718050376.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/>公司以投资建设、连锁加盟经营管理的“怡养爱晚连锁基地”提供“养生、养老、休闲、度假、旅游”五位一体的专业化服务，拥有符合混合年龄群体心态特征的公寓、洋房、别墅等住宅，并配备餐饮、文化娱乐、医疗保健等多项服务设施。</p><p>创新养老产业运作模式——“租赁养老”。在销售养生、养老、休闲、度假、旅游房产的同时，增加“经营性租赁”概念（房屋租赁；养生、养老、休闲、度假、旅游服务租赁）。&nbsp;<br/>创新养生、养老、休闲、度假、旅游投资和消费模式——“零风险投资实现零费用中高端养老消费”。对客户缴纳的养生、养老、休闲、度假、旅游的房屋及其服务租赁保证金，通过国家法律的保护、第三方担保、房产抵押实现客户零风险投资，同时实现客户零费用的中高端养生、养老、休闲、度假、旅游消费。&nbsp;<br/>创新养生、养老、休闲、度假、旅游用房的权属模式——“租赁购买相结合”。租赁（获取房屋使用权）、购买（获取房屋所有权），既分离又统一；或先分离，再合二为一。提倡房产不卖一套，只卖一份。让一份份的养生、养老、休闲、度假、旅游房产的使用权能够流动，形成产业化的模式。</p><p>&nbsp;</p><p>与国内顶级医院、医科大学合作，设立健康管理及计算机健康医疗远程会诊中心，提供优质的远程诊断、建立健康档案等健康管理服务；专家定期进驻怡养爱晚连锁基地。&nbsp;<br/>引进国外先进的养老建筑设计及养老管理服务理念——美国流行的CCRC（Continuing Care Retirement Community）养老社区模式及理念，即以硬件住宅为基础，软件服务为依托，专业机构为支撑的结合互补的养老模式，从根本上延续和优化了老年人的居家习惯。</p><p>由专业的酒店管理团队提供优质的服务。</p><p>&nbsp;</p><p>倡导“乐活”LOHAS（Lifestyles Of Health and Sustainability）精神，推行健康、绿色消费概念，以自然健康的养老旅游产品，精致优良的休闲养生环境，专业热诚的全方位服务，引领客户体验“乐活LOHAS”的养生、养老、休闲、度假、旅游生活方式。&nbsp;<br/>通过怡养爱晚（兴隆钓鱼台）连锁基地项目的规范建设及运营管理示范效应，将逐步形成全国连锁，异地互动。计划到2018年五年间，总投资50亿元，在全国建设并拥有150万平米养生养老休闲度假用房，按平均每套用房75平米，折算20000套用房，每年可满足全国50000名左右中高端客户群体的需求。</p><p>飞易腾公司是专业从事网站策划、网站制作、品牌营销、软件开发等互联网应用产品的高新企业，业务遍及北京、河北、陕西、内蒙等区域，凭借着公司实力以及专业的精神，经过激烈的角逐后，取得了世纪爱晚公司领导的认可，承担起怡养爱晚门户网站的策划与建设工作。我们一定不负所托，做到大家满意。 &nbsp; &nbsp;</p><p><br/></p>', 620, 257, 262, 597, '2018-12-13 15:10:37', '2016-12-22 16:57:59', '2017-09-12 16:45:34', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1022, 1023, 0, '我国卫生信息化建设扎实推进', NULL, '我国卫生信息化建设扎实推进-北京网站建设', '北京网站建设', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-WSXXH1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, NULL, '<p>&nbsp; &nbsp; &nbsp; 近日，医学装备与临床信息化应用技术发展研讨会在陕西省西安市召开。国家卫生和计划生育委员会规划信息司有关负责人在会上介绍，我国卫生信息化建设总体框架已初步建立。截至目前，已有内蒙古、江苏、河南、湖北、四川等15个试点地区启动居民健康卡建设工作，统筹已有就诊卡、社保卡、新农合卡等，并逐步应用在预约挂号、就诊、住院结算与儿童免疫等环节。&nbsp;</p><p>据介绍，2010年以来，中央财政连续3年安排转移支付地方专项资金，地方各级政府加大投入力度，从区域卫生信息平台、远程会诊、公立医院信息化管理、基层医疗机构信息化能力建设等不同角度推动“3521工程”落地。“3521工程”即建设国家、省、区域（地市或县级）三级卫生信息平台，加强公共卫生、医疗服务、医疗保障、药品供应保障和综合管理5项业务应用系统，建设居民电子健康档案、电子病历2个基础数据库和1个业务网络。 北京飞易腾作为专业网站建设、软件开发等换联网信息化应用开发的企业为国家医疗卫生信息化建设保驾护航。</p><p>目前，卫生信息化建设总体框架初步显现。一是区域卫生信息平台建设进展顺利。内蒙古、上海、江苏、安徽等10个省（区、市）已经不同程度建立省级卫生综合管理信息平台，134个地市和397个县区不同程度地建立了区域卫生信息平台，实现区域内卫生信息实时采集与共享。二是重点业务应用系统功能进一步深化。上海、浙江实现公共卫生信息系统与医院信息系统、区域卫生信息平台的互联互通。三是部分地区实现电子健康档案与电子病历资源共享。北京、上海、安徽、重庆等12个省（区、市）建立电子健康档案资源库，上海、江苏、浙江等地的部分医院可通过省级卫生信息平台实现电子病历共享。四是居民健康卡建设稳步推进。五是信息标准和安全体系建设日益完善。同时，人口计划生育信息化取得积极进展，90%以上的县、乡级人口计生部门依托人口宏观管理与决策信息系统开展日常管理与服务工作。该会议由中国医学装备协会主办。&nbsp;</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-WSXXH2.jpg\" title=\"10-24-08-27-70.jpg\" alt=\"10-24-08-27-70.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p>', 411, 117, 120, 388, '2018-12-15 08:04:36', '2016-12-22 17:00:44', '2017-09-12 16:37:59', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1023, 1023, 0, '端午节放假通知', NULL, '端午节放假通知', '北京网站制作', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/2013-duanwu1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '2013端午节放假通知', '<p>&nbsp; &nbsp; &nbsp;在端午节来临之际，飞易腾公司预祝各位员工以及广大客户朋友们节日快乐，身体健康，万事如意！提醒端午节期间高速不免费，假期少贪杯。</p><p>&nbsp; &nbsp;&nbsp;飞易腾公司2013端午节放假安排如下：2013年6月10日（周一）、6月11日（周二）、6月12日（周三）放假三天，6月8日（周六），6月9日（周日）上班；假期期间有任何网站相关情况可拨打电话18911006747 &nbsp;杨经理</p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/2013-duanwu2.jpg\" title=\"duanwu1.jpg\" alt=\"duanwu1.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" style=\"width: 500px; height: 300px;\"/></p>', 456, 137, 138, 433, '2018-12-13 23:17:44', '2016-12-22 17:02:16', '2017-09-12 16:34:38', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1024, 1023, 0, '祝贺IOS培训网站-果客网成功上线', NULL, '祝贺IOS培训网站-果客网成功上线', '北京网站制作', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/guokewang1.gif', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '祝贺IOS培训网站-果客网成功上线！ 网址：http://www.appleker.com.cn?   培训网站制作特点：篇幅大、设计要素多、框架栏目规划要合理。', '<p>祝贺IOS培训网站-果客网成功上线！ 网址：http://www.appleker.com.cn&nbsp;&nbsp;&nbsp;培训网站制作特点：篇幅大、设计要素多、框架栏目规划要合理。</p><p>CEMD（全国移动开发工程师认证考试）：是在工业和信息化部的组织指导下，由中国电子学会、工业信息化部人才交流中心、国家653工程办公室联合举办，全权负责组织的认证考试，是针对我国日益增长的移动计算、移动商务、移动娱乐等领域的人才需要，培养政府认证、社会需要、企业认可的移动互联网开发实用型人才。</p><p style=\"text-align: center;\">&nbsp;<img src=\"http://img.feiyit.com/feiyit/website/news/guokewang2.gif\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 500px; height: 300px;\"/></p><p>目前，CEMD在移动开发工程师方面，主要针对两个平台，分别为iOS平台和Android平台，其中，由果客网全面负责iOS平台开发培训的教材编写，负责把握iOS开发培训的教学大纲。同时，果客网作为CEMD移动开发工程师实训基地，为工业和信息化部全国移动开发工程师（iOS）指定培训机构。</p><p><br/></p><p>培训网站BANNER</p><p><br/></p>', 466, 149, 150, 443, '2018-12-13 23:06:38', '2016-12-22 17:04:59', '2017-09-12 16:30:57', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1025, 1023, 0, '祝贺物流网站-亚之星成功上线', NULL, '祝贺物流网站-亚之星成功上线', '北京网站制作', '飞易腾', b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/news/feiyit-yazhixing1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '      祝贺物流网站-亚之星成功上线? ！物流公司小企业比较多，一般都通过百度竞价的方式来推广，但是百度搜索北京物流等关键词，打开以后都是大同小异的网站，模板型网站，浪费了很多的客户流量。', '<p>&nbsp; &nbsp; &nbsp; 祝贺物流网站-亚之星成功上线&nbsp;！物流公司小企业比较多，一般都通过百度竞价的方式来推广，但是百度搜索北京物流等关键词，打开以后都是大同小异的网站，模板型网站，浪费了很多的客户流量。</p><p>&nbsp; &nbsp;&nbsp;北京亚之星物流有限公司是一家集信息咨询、物流服务、仓储配送、全程运输、电子商务结算为一体的现代化北京物流公司，有多年全国公路运输的专业成功经验。具有决对优势的运输价格，且已形成较为完善的运输网络。提供全方面“提---运---送”一站式门对门服务，可代收货款、运费到付、异地调货等服务。公司领导非常注重信息化的建设，认识到网站是公司在网络上的品牌形象的重要性，一心要把公司网站做成物流行业的标志性网站。飞易腾公司经过详细分析，策划，网站终于成功上线了，得到了亚之星公司领导的认可。</p><p>物流网站banner设计<br/></p><p style=\"text-align: center;\"><img src=\"http://img.feiyit.com/feiyit/website/news/feiyit-yazhixing2.jpg\" width=\"500\" height=\"300\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 500px; height: 300px;\"/></p><p><br/></p><p><br/></p>', 457, 155, 157, 434, '2018-12-15 02:29:18', '2016-12-22 17:11:30', '2017-09-12 16:27:56', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
INSERT INTO `cms_article` VALUES (1026, 1023, 0, '飞易腾签约联创得力公司网站建设项目', NULL, NULL, '管理员', '飞易腾', b'0', NULL, NULL, 1, NULL, NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, '联创得力网络技术（北京）有限公司是由行业内部资深团队联手创办的以sem,sem托管，sem优化，sem账户外包等。', '<p>北京联创得力科技有限公司是一家专业品牌传播策划公司,主要业务有：网站优化、网络推广、网站托管、竞价托管、口碑营销传播,致力于为中小企业提供全面有效的互联网营销服务！万象东方·传播品牌价值.</p><p>公司主要服务于中小企业，提供网站策划，网站设计制作建设，网络推广营销于一体的专业服务。公司以“&nbsp;以“网络营销推广”为理念，企业“传播品牌价值”为宗旨，专注于网络平台的新营销领域。希望通过我们的专业水平和不懈努力，重塑中小企业网络形象，为企业产品推广，文化建设传播提供服务指导。三年来，公司一直秉承以用户需求为核心，挖掘全国各地区市场同时，为超过一百家中小企业提供网站策划，SEO优化服务，优质、用心的服务赢得了众多企业的信赖和好评，逐渐树立起公司良好品牌。</p><p>公司不仅仅提供专业的网络营销服务，同时还建立了完善的售后服务体系，为企业发展中遇到的问题和困难提供指导帮助。我们相信，通过我们的不断努力和追求，一定能够实现与中小企业的互利共赢！</p><p>效果图展示：</p><p><br/></p><p><br/></p>', 443, 141, 145, 420, '2018-12-13 02:06:22', '2016-12-22 17:12:57', '2016-12-22 17:24:57', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
