INSERT INTO `cms_article` VALUES (1392, 1021, 0, '【 小牛卡】微信会员管理 ', NULL, '会员管理【微信】', '管理员', NULL, b'0', NULL, NULL, 1, 'http://img.feiyit.com/feiyit/website/cases/xiaoniuka1.jpg', NULL, NULL, b'0', b'0', b'0', b'0', b'0', b'0', b'0', b'1', NULL, ' 小牛卡是微信公众号里面的商家集结地，用户可以购买会员到合作商家消费</br>\r\n商家可通过地图浏览就近商家</br>\r\n可查看商家介绍，地址，联系电话，乘车路线等。</br>\r\n在公众号可充值会员积分，查看自己的消费订单以及充值订单等。', '<p><img src=\"http://img.feiyit.com/feiyit/website/cases/xiaoniuka2.jpg\" width=\"1102\" height=\"5885\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 1102px; height: 5885px;\"/></p>', 3741, 3215, 3216, 3718, '2018-12-15 04:15:43', '2017-09-11 17:06:23', '2017-09-11 17:06:23', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
