INSERT INTO `cms_article` VALUES (1401, 1019, 0, '【买手令】全球购APP', NULL, '海外购【APP】', '管理员', NULL, b'0', NULL, 'APP,微信端', 1, 'http://img.feiyit.com/feiyit/website/cases/maishouling.jpg', NULL, NULL, b'1', b'0', b'0', b'0', b'0', b'0', b'0', b'1', 'APP,微信端', '出国购物最实用的App，日本、韩国、香港、美国、澳洲、欧洲，什么值得买全知道。\n　　买东西不用自己做攻略，有全球的小伙伴一起帮忙推荐。Instagram、vogue时尚达人，海外留学生，全球的白瘦美，身边好品味的闺蜜都在偷偷玩的App', '<p><img src=\"http://img.feiyit.com/feiyit/website/cases/maishoulingda.jpg\" title=\"买手令1.jpg\" alt=\"买手令1.jpg\" width=\"1101\" height=\"5696\" border=\"0\" vspace=\"0\" style=\"width: 1101px; height: 5696px;\"></p>', 3770, 3266, 3267, 3747, '2018-12-11 04:29:30', '2018-12-18 00:00:00', '2017-09-12 13:39:30', NULL, 'cf15bf0a-d8b1-4643-9dfe-8601b5bd2299');
