-- ----------------------------
-- Table structure for cms_imgtype
-- ----------------------------
DROP TABLE IF EXISTS `cms_imgtype`;
CREATE TABLE `cms_imgtype`  (
  `Guid` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `ParentGuid` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '一级菜单',
  `Type` tinyint(4) NOT NULL DEFAULT 0 COMMENT '图片类型分类 0=本地 1=云端',
  `Level` tinyint(10) NOT NULL DEFAULT 1 COMMENT '级别',
  `Name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `EnName` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `AddDate` datetime(0) NOT NULL ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`Guid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

