DROP TABLE IF EXISTS `cms_tags`;
CREATE TABLE `cms_tags`  (
  `Guid` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `FirstLetter` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '首字母',
  `Name` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标签名称',
  `Status` bit(1) NOT NULL DEFAULT b'1' COMMENT '是否启用',
