-- ----------------------------
-- Table structure for cms_vote
-- ----------------------------
DROP TABLE IF EXISTS `cms_vote`;
CREATE TABLE `cms_vote`  (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自增',
  `ColumnId` int(11) NOT NULL COMMENT '所属栏目ID',
  `Title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票标题',
  `SubTitle` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票副标题',
  `ItemSum` int(11) NOT NULL COMMENT '选项总数',
  `VoteSum` int(11) NOT NULL COMMENT '投票总数',
  `Options` bit(1) NOT NULL,
  `VoteType` int(11) NOT NULL COMMENT '投票类型',
  `ImgUrl` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '宣传图',
  `IsTime` bit(1) NOT NULL COMMENT '时间限制',
  `BeginDate` datetime(0) NOT NULL COMMENT '开始时间',
  `EndDate` datetime(0) NOT NULL,
  `Summary` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票简介',
  `AddDate` datetime(0) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`Id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for cms_voteitem
-- ----------------------------
DROP TABLE IF EXISTS `cms_voteitem`;
CREATE TABLE `cms_voteitem`  (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自增',
  `VoteId` int(11) NOT NULL COMMENT '投票项ID',
  `Title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票项',
  `VoteSum` int(11) NOT NULL COMMENT '投票数',
  `Scale` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `Sort` int(11) NOT NULL DEFAULT 0 COMMENT '排序字段',
  PRIMARY KEY (`Id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for cms_votelog
-- ----------------------------
DROP TABLE IF EXISTS `cms_votelog`;
CREATE TABLE `cms_votelog`  (
  `Guid` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '唯一ID',
  `VoteId` int(11) NULL DEFAULT NULL COMMENT '投票ID',
  `ItemId` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票项ID',
  `UserId` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '投票人ID',
  `UserName` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票人昵称',
  `Ip` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'IP地址',
  `AddDate` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '投票时间',
  `Summary` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '投票详情',
  PRIMARY KEY (`Guid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for form_table
-- ----------------------------
DROP TABLE IF EXISTS `form_table`;
CREATE TABLE `form_table`  (
  `Guid` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '表单唯一编号',
  `Name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '表单名称',
