  `TableJson` varchar(5000) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '表结构',
  `IsDel` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否删除',
  `AddDate` datetime(0) NOT NULL COMMENT '表单创建时间',
  `UpdateDate` datetime(0) NOT NULL COMMENT '表单修改时间',
  PRIMARY KEY (`Guid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

