  `IsDel` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否删除',
  `Status` bit(1) NOT NULL DEFAULT b'0' COMMENT '状态 状态',
  `LoginTime` datetime(0) NULL DEFAULT NULL COMMENT '登录时间 登录时间',
  `LoginSum` int(11) NOT NULL DEFAULT 0 COMMENT '登录次数 登录次数',
  `RegTime` datetime(0) NOT NULL COMMENT '注册时间 注册时间',
  PRIMARY KEY (`Guid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '社区用户表 ' ROW_FORMAT = Dynamic;

